//input page for fetching the 2 files from the user 
import {Button,Alert,AlertTitle} from '@mui/material';
import React, { Component } from "react";

//import ReactFileReader from "react-file-reader";
class Manipulation extends Component {
  constructor(props) {
    super(props);
  }
  
//file from user 
  showFile = async (e) => {
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      console.log(text);
      alert(text);
    };
    reader.readAsText(e.target.files[0]);
  };
  fetchdcndata = async (e) => {
    try {
      let dcnfile = await fetch(
        "https://bafybeiacs6qaawbgrwq26zzno2lpoor3nzzmjyo5b4f2ejbjfdp5n2of74.ipfs.w3s.link/file3.json"
      ).then((x) => x.text());

      console.log(dcnfile);
      alert(dcnfile);
      return await dcnfile;
    } catch (error) {
      console.log(error);
    }
  };
  render = () => {
    return (
      <div>
        <input type="file" onChange={(e) => this.showFile(e)} />
       
<br/>

        <Alert severity="info">
  <AlertTitle>Info</AlertTitle>
  Click here to fetch the file from decentralised storage </Alert>
       {/* <input type="file" onChange={(e) => this.showFile(e)} /> */}
        
        <Button  variant="contained" onClick={(e) => {this.fetchdcndata(e)
    
  }}>Fetch orginal</Button>
      </div>
    );
  };
}

export default Manipulation;
