import React from "react";
/*

Function is used for 

 */
const UpdateStrapi = async (updaretype) => {

  var myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");
  let editType = "invest";
  var raw = JSON.stringify({
    data: {
      fileID: "fifth file ID ",
      cid: "bafybeiacs6qaawbgrwq26zzno2lpoor3nzzmjyo5b4f2ejbjfdp5n2of74",
      name: "file3.json",
      type: editType,
    },
  });
  var requestOptions = {
    method: "POST",
    headers: myHeaders,
    body: raw,
    redirect: "follow",
  };
  console.log("from updatestrapi");
  console.log(raw);

  let cid = await fetch("http://localhost:1337/api/fileinfos", requestOptions)
    .then((x) => x.json())
    .catch(console.log(Error));
  console.log(cid);
};

export default UpdateStrapi;

