    reader.addEventListener(
      "load",
      () => {
        // this will then display a text file
        //content.innerText = reader.result;
        console.log(reader.result);
      },
      false
    );

    if (file) {
      reader?.readAsText(file);
    }
  }

  return (
    <div>
      <h1>testinggg</h1>
      <input type="file" onchange={ppreviewFile()} />
      <br />
      <p class="content"></p>
    </div>
  );
};
