//input page for fetching the 2 files from the user 
import differenceGenerator from '../../../logic/differgenerator'
import React, { Component } from "react";

//import ReactFileReader from "react-file-reader";
class Manipulation extends Component {
  constructor(props) {
    super(props);
  }

  showFile = async (e) => {
    //Fetching file data from Decenralized network 
    async function fetchdcndata() {
      try {
        let dcnfile = await fetch(
          "https://bafybeiacs6qaawbgrwq26zzno2lpoor3nzzmjyo5b4f2ejbjfdp5n2of74.ipfs.w3s.link/file3.json"//fetch it from db 
        ).then((x) => x.text());
  
        console.log(dcnfile);
        alert(dcnfile);
        return await dcnfile;
        
      } catch (error) {
        console.log(error);
      }
    }
    //Fetching Uploaded file data  
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      console.log(text);
      alert(text);

    };
    reader.readAsText(e.target.files[0]);
  };

  render = () => {
    return (
      <div>
        <input type="file" onChange={(e) => this.showFile(e)} />

        <input type="file" onChange={(e) => this.showFile(e)} />

        <button onClick={fetchdcndata}>Default</button>;

      </div>
    );
  };
}

export default Manipulation;
